-- Function: criatabela()-- DROP FUNCTION criatabela();CREATE OR REPLACE FUNCTION criatabela()  RETURNS integer AS$BODY$DECLARE foiCriada INTEGER :=0;tabela RECORD;BEGIN  SELECT INTO tabela tablename FROM pg_tables where tablename='clientes' and schemaname = ANY (current_schemas(true)); IF tabela.tablename IS NULL THEN  CREATE TABLE clientes(id integer NOT NULL,nome text,endereco text,versao integer,ativo integer,cidade integer,dataCadastro timestamp without time zone[], CONSTRAINT clientes_pkey PRIMARY KEY (id))WITH (  OIDS=FALSE); foiCriada = 1; END IF; RETURN foiCriada; END; $BODY$  LANGUAGE plpgsql VOLATILE  COST 100;ALTER FUNCTION criatabela() OWNER TO postgres;